import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Iproduct } from 'src/app/Models/iproduct';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css'],
})
export class AdminDashboardComponent implements OnInit {
  products: Iproduct[] = [];

  constructor(private productService: ProductService, private router: Router) {}

  ngOnInit(): void {
    // Fetch products of a specific category, e.g., category with ID 1
    this.productService.allProducts.subscribe((data) => {
      this.products = data;
    });
  }

  addProduct(): void {
    this.router.navigate(['/product-details', 'new']);
  }

  editProduct(product: Iproduct): void {
    this.router.navigate(['/product-details', product.id]);
  }

  deleteProduct(product: Iproduct): void {
    if (confirm('Are you sure you want to delete this product?')) {
      this.productService.deleteProduct(product.id).subscribe(() => {
        this.products = this.products.filter((p) => p.id !== product.id);
      });
    }
  }
}
<div class="container mt-4">
  <h2>Product List</h2>
  <button class="btn btn-primary" (click)="addProduct()">
    Add New Product
  </button>
  <ul class="list-group mt-3">
    <li
      class="list-group-item d-flex justify-content-between align-items-center"
      *ngFor="let product of products"
    >
      <div class="d-flex align-items-center">
        <img
          class="m-3"
          [src]="product.PrdImgURL"
          alt="Product Image"
          width="100"
          height="100"
        />
        <div>
          <h5 class="mb-1">{{ product.name }}</h5>
          <p class="mb-1">Price: {{ product.price }}</p>
        </div>
      </div>
      <div>
        <button class="btn btn-sm btn-info m-2" (click)="editProduct(product)">
          Edit
        </button>
        <button
          class="btn btn-sm btn-danger m-2"
          (click)="deleteProduct(product)"
        >
          Delete
        </button>
      </div>
    </li>
  </ul>
</div>
